let slots = flow.get("slots");
let parkingRecords = flow.get("parkingRecords") || [];
let reservations = flow.get("reservations") || [];

let slotId = msg.payload.slotId;
let plateNumber = msg.payload.plateNumber;
let vehicleType = msg.payload.vehicleType || "Car";

// Validate input
if (!plateNumber || plateNumber.trim() === "") {
    node.warn("No plate number provided");
    return null;
}

plateNumber = plateNumber.toUpperCase().trim();

// Check if slot exists and is available or reserved for this vehicle
if(slots && slots[slotId]){
    let canPark = false;
    let reservationMatch = false;

    if(slots[slotId].status === "Available") {
        canPark = true;
    } else if(slots[slotId].status === "Reserved" && slots[slotId].reservedBy === plateNumber) {
        canPark = true;
        reservationMatch = true;
        // Remove the reservation
        reservations = reservations.filter(r => !(r.slotId == slotId && r.plateNumber === plateNumber));
    }

    if(canPark) {
        let entryTime = new Date();

        // Update slot status
        slots[slotId].status = "Occupied";
        slots[slotId].plateNumber = plateNumber;
        slots[slotId].entryTime = entryTime.toLocaleString();
        slots[slotId].exitTime = null;
        slots[slotId].vehicleType = vehicleType;
        slots[slotId].reservedUntil = null;
        slots[slotId].reservedBy = null;

        // Create parking record
        let record = {
            id: Date.now(),
            plateNumber: plateNumber,
            slotId: slotId,
            vehicleType: vehicleType,
            entryTime: entryTime.toISOString(),
            entryTimeDisplay: entryTime.toLocaleString(),
            exitTime: null,
            exitTimeDisplay: null,
            duration: null,
            status: "Parked",
            wasReserved: reservationMatch
        };

        // Add to database
        parkingRecords.push(record);

        // Update flow context
        flow.set("slots", slots);
        flow.set("parkingRecords", parkingRecords);
        flow.set("reservations", reservations);

        node.status({fill:"green",shape:"dot",text:`${plateNumber} parked in slot ${slotId}`});

        return {payload: slots, parkingRecord: record};
    } else {
        node.warn(`Slot ${slotId} is not available for ${plateNumber}`);
        return null;
    }
} else {
    node.warn(`Slot ${slotId} does not exist`);
    return null;
}

let slots = flow.get("slots");
let parkingRecords = flow.get("parkingRecords") || [];
let vehiclePricing = flow.get("vehiclePricing") || {};

let slotId = msg.payload;

// Check if slot exists and is occupied
if(slots && slots[slotId] && slots[slotId].status === "Occupied"){
    let exitTime = new Date();
    let plateNumber = slots[slotId].plateNumber;
    let vehicleType = slots[slotId].vehicleType || "Car";

    // Find the parking record
    let recordIndex = parkingRecords.findIndex(r => 
        r.plateNumber === plateNumber && 
        r.slotId == slotId && 
        r.status === "Parked"
    );

    if (recordIndex !== -1) {
        // Calculate duration and pricing
        let entryTime = new Date(parkingRecords[recordIndex].entryTime);
        let durationMs = exitTime - entryTime;
        let durationHours = Math.floor(durationMs / (1000 * 60 * 60));
        let durationMinutes = Math.floor((durationMs % (1000 * 60 * 60)) / (1000 * 60));
        let durationText = `${durationHours}h ${durationMinutes}m`;

        // Calculate pricing based on vehicle type and duration
        let pricing = vehiclePricing[vehicleType] || { basePrice: 30, multiplier: 1.0 };
        let totalHours = durationMs / (1000 * 60 * 60);
        let price = Math.round(pricing.basePrice * pricing.multiplier * Math.ceil(totalHours));

        // Update parking record
        parkingRecords[recordIndex].exitTime = exitTime.toISOString();
        parkingRecords[recordIndex].exitTimeDisplay = exitTime.toLocaleString();
        parkingRecords[recordIndex].duration = durationText;
        parkingRecords[recordIndex].status = "Completed";
        parkingRecords[recordIndex].price = price;
    }

    // Clear slot
    slots[slotId].status = "Available";
    slots[slotId].plateNumber = null;
    slots[slotId].entryTime = null;
    slots[slotId].exitTime = exitTime.toLocaleString();
    slots[slotId].vehicleType = null;
    slots[slotId].reservedUntil = null;
    slots[slotId].reservedBy = null;

    // Update flow context
    flow.set("slots", slots);
    flow.set("parkingRecords", parkingRecords);

    node.status({fill:"blue",shape:"dot",text:`${plateNumber} exited from slot ${slotId}`});

    return {payload: slots, exitRecord: parkingRecords[recordIndex]};
} else {
    node.warn(`Slot ${slotId} is not occupied`);
    return null;
}

// Record actual user parking data to CSV file
// This matches the format of IIoT_Smart_Parking_Management.csv

let slots = flow.get("slots") || {};
let parkingRecords = flow.get("parkingRecords") || [];

// Get the most recent parking action
let recentRecord = parkingRecords[parkingRecords.length - 1];
if (!recentRecord) {
    return null; // No parking action to record
}

// Count current occupancy
let occupied = Object.values(slots).filter(s => s.status === "Occupied").length;
let reserved = Object.values(slots).filter(s => s.status === "Reserved").length;
let totalSlots = 6;
let occupancyRate = (occupied / totalSlots).toFixed(4);

// Determine occupancy status
let occupancyStatus = recentRecord.status === "Parked" ? "Occupied" : "Vacant";

// Create CSV line matching IIoT_Smart_Parking_Management.csv format
let timestamp = new Date();
let entryHour = recentRecord.entryTime ? new Date(recentRecord.entryTime).getHours() : 0;
let exitHour = recentRecord.exitTime ? new Date(recentRecord.exitTime).getHours() : 0;
let duration = recentRecord.duration ? recentRecord.duration.replace('h', '').replace('m', '').trim().split(' ')[0] : 0;
let paymentStatus = recentRecord.status === "Completed" ? "Paid" : "Unpaid";
let vehicleType = recentRecord.vehicleType || "Car";

// Format: Timestamp,Parking_Spot_ID,Occupancy_Status,Vehicle_Type,User_Type,Reserved_Status,Occupancy_Rate,Entry_Time,Exit_Time,Parking_Duration,Payment_Status,plate_number
let csvLine = `${timestamp.toISOString().replace('T', ' ').replace('Z', '')},${recentRecord.slotId},${occupancyStatus},${vehicleType},Registered,${reserved > 0 ? 1 : 0},${occupancyRate},${entryHour},${exitHour},${duration},${paymentStatus},${recentRecord.plateNumber}`;

node.status({fill:"blue",shape:"dot",text:`Recording: ${occupancyStatus} (${occupied}/${totalSlots})`});

// Send CSV line to file writer
return {
    payload: csvLine,
    topic: "record_parking"
};

let slots = flow.get("slots");
let reservations = flow.get("reservations") || [];

let slotId = msg.payload.slotId;
let plateNumber = msg.payload.plateNumber;
let reservationDuration = 15; // 15 minutes

// Validate input
if (!plateNumber || plateNumber.trim() === "") {
    node.warn("No plate number provided for reservation");
    return null;
}

plateNumber = plateNumber.toUpperCase().trim();

// Check if slot exists and is available
if(slots && slots[slotId] && slots[slotId].status === "Available"){
    let now = new Date();
    let reservedUntil = new Date(now.getTime() + (reservationDuration * 60000));

    // Update slot status
    slots[slotId].status = "Reserved";
    slots[slotId].reservedBy = plateNumber;
    slots[slotId].reservedUntil = reservedUntil.toLocaleString();

    // Create reservation record
    let reservation = {
        id: Date.now(),
        slotId: slotId,
        plateNumber: plateNumber,
        reservedAt: now.toISOString(),
        reservedUntil: reservedUntil.toISOString(),
        status: "Active"
    };

    reservations.push(reservation);

    // Update flow context
    flow.set("slots", slots);
    flow.set("reservations", reservations);

    node.status({fill:"yellow",shape:"dot",text:`Slot ${slotId} reserved for ${plateNumber}`});

    return {payload: slots, reservation: reservation};
} else {
    node.warn(`Slot ${slotId} is not available for reservation`);
    return null;
}

let reservation = msg.reservation;
if (!reservation) return null;

// Set a timeout to expire the reservation
let timeoutMs = 15 * 60 * 1000; // 15 minutes

setTimeout(() => {
    let slots = flow.get("slots");
    let reservations = flow.get("reservations") || [];

    // Check if reservation is still active and not used
    if (slots[reservation.slotId] && slots[reservation.slotId].status === "Reserved" && 
        slots[reservation.slotId].reservedBy === reservation.plateNumber) {

        // Expire the reservation
        slots[reservation.slotId].status = "Available";
        slots[reservation.slotId].reservedBy = null;
        slots[reservation.slotId].reservedUntil = null;

        // Update reservation status
        let resIndex = reservations.findIndex(r => r.id === reservation.id);
        if (resIndex !== -1) {
            reservations[resIndex].status = "Expired";
        }

        flow.set("slots", slots);
        flow.set("reservations", reservations);

        // Trigger dashboard update
        node.send({payload: slots});
    }
}, timeoutMs);

return null;

let parkingRecords = flow.get("parkingRecords") || [];
let overstayAlerts = flow.get("overstayAlerts") || [];
let maxDurationHours = 24; // 24 hours maximum

let now = new Date();
let overstayVehicles = [];

// Check all currently parked vehicles
parkingRecords.forEach(record => {
    if (record.status === "Parked") {
        let entryTime = new Date(record.entryTime);
        let durationMs = now - entryTime;
        let durationHours = durationMs / (1000 * 60 * 60);

        if (durationHours > maxDurationHours) {
            overstayVehicles.push({
                plateNumber: record.plateNumber,
                slotId: record.slotId,
                entryTime: record.entryTime,
                durationHours: Math.round(durationHours * 100) / 100,
                vehicleType: record.vehicleType
            });
        }
    }
});

// Create alerts for new overstay vehicles
overstayVehicles.forEach(vehicle => {
    let existingAlert = overstayAlerts.find(alert => 
        alert.plateNumber === vehicle.plateNumber && 
        alert.slotId === vehicle.slotId &&
        alert.status === "Active"
    );

    if (!existingAlert) {
        let alert = {
            id: Date.now() + Math.random(),
            plateNumber: vehicle.plateNumber,
            slotId: vehicle.slotId,
            vehicleType: vehicle.vehicleType,
            entryTime: vehicle.entryTime,
            durationHours: vehicle.durationHours,
            alertTime: now.toISOString(),
            status: "Active",
            severity: vehicle.durationHours > 48 ? "Critical" : "Warning"
        };

        overstayAlerts.push(alert);
    }
});

flow.set("overstayAlerts", overstayAlerts);

if (overstayVehicles.length > 0) {
    node.status({fill:"red",shape:"dot",text:`${overstayVehicles.length} overstay alerts`});
    return {payload: overstayVehicles, alerts: overstayAlerts};
} else {
    node.status({fill:"green",shape:"dot",text:"No overstay detected"});
    return null;
}

let reservations = flow.get("reservations") || [];

// Filter active reservations
let activeReservations = reservations.filter(r => r.status === "Active");

// Sort by reservation time (most recent first)
activeReservations.sort((a, b) => new Date(b.reservedAt) - new Date(a.reservedAt));

return {payload: activeReservations};

