let occupancyRate = msg.occupancyRate || 0;
let basePrice = 30;
let multiplier = 1 + (occupancyRate / 100);
let price = Math.round(basePrice * multiplier);
flow.set("currentPrice", price);
return {payload: price};

// Parse Python ML script output and format for dashboard
let pythonOutput = msg.payload;

node.log('Python ML output (length=' + (pythonOutput ? pythonOutput.length : 0) + '): ' + pythonOutput);

// Check if output is empty
if (!pythonOutput || pythonOutput.trim() === '') {
    node.warn("Python returned empty output. Check stderr for errors.");
    return null;
}

// Try to parse JSON output from Python
let predictionResult;
try {
    predictionResult = JSON.parse(pythonOutput);
} catch (e) {
    node.warn("Failed to parse Python output: " + pythonOutput);
    node.warn("Parse error: " + e.message);
    return null;
}

// Check for errors from Python
if (predictionResult.error) {
    node.warn("Python ML error: " + predictionResult.error);
    return null;
}

// Get current slot data for accurate availability
let slots = flow.get("slots") || {};
let occupied = Object.values(slots).filter(s => s.status === "Occupied").length;
let reserved = Object.values(slots).filter(s => s.status === "Reserved").length;
let totalSlots = 6;
let availableNow = totalSlots - occupied - reserved;

// Convert Python ML output to dashboard format
let isOccupied = predictionResult.predicted_occupancy === 1;
let confidence = predictionResult.confidence || 0.5;
let probabilityVacant = predictionResult.probability_vacant || 0;
let probabilityOccupied = predictionResult.probability_occupied || 0;

// Determine trend based on probability
let trend = "stable";
if (probabilityOccupied > 0.7) {
    trend = "increasing";
} else if (probabilityVacant > 0.7) {
    trend = "decreasing";
}

// Create recommendation based on ML prediction
let recommendation = "";
if (availableNow > 0) {
    recommendation = `ü§ñ ML: ${availableNow} slots available now! Prediction: ${predictionResult.prediction_label} (${Math.round(confidence * 100)}% confidence)`;
} else if (!isOccupied && probabilityVacant > 0.6) {
    recommendation = `ü§ñ ML: Currently full, but likely to have space soon (${Math.round(probabilityVacant * 100)}% chance)`;
} else {
    recommendation = `ü§ñ ML: No spots available. High occupancy predicted (${Math.round(probabilityOccupied * 100)}% chance)`;
}

let dashboardResult = {
    "can_park_now": availableNow > 0,
    "available_slots_now": availableNow,
    "predicted_available_30min": isOccupied ? Math.max(0, availableNow - 1) : Math.min(totalSlots, availableNow + 1),
    "total_slots": totalSlots,
    "trend": trend,
    "recommendation": recommendation,
    "confidence": confidence > 0.8 ? "High" : (confidence > 0.6 ? "Medium" : "Low"),
    "data_points": 1,
    "ml_prediction": true,
    "python_confidence": confidence,
    "probability_vacant": probabilityVacant,
    "probability_occupied": probabilityOccupied,
    "prediction_label": predictionResult.prediction_label,
    "prediction_time": predictionResult.prediction_time || new Date().toISOString(),
    "model_status": "‚úÖ Trained ML Model Active",
    "current_occupancy_rate": Math.round((occupied / totalSlots) * 100),
    "reserved_slots": reserved
};

node.status({fill:"green",shape:"dot",text:"ML Prediction: " + predictionResult.prediction_label});
return {payload: dashboardResult};

